function o(){chrome.windows.create({url:"https://everything-pilot.vercel.app/extension-login",type:"popup",width:480,height:620})}chrome.runtime.onStartup.addListener(()=>{chrome.storage.local.get("jwt",e=>{e.jwt?console.log("🔐 Token found:",e.jwt.slice(0,10)+"..."):(console.log("🔒 No token found. Triggering login popup."),o())})});chrome.runtime.onInstalled.addListener(()=>{chrome.storage.local.get("jwt",e=>{e.jwt||(console.log("🔒 First-time install: Opening login popup."),o())})});
